{"metadata":{"kernelspec":{"language":"python","display_name":"Python 3","name":"python3"},"language_info":{"pygments_lexer":"ipython3","nbconvert_exporter":"python","version":"3.6.4","file_extension":".py","codemirror_mode":{"name":"ipython","version":3},"name":"python","mimetype":"text/x-python"}},"nbformat_minor":4,"nbformat":4,"cells":[{"cell_type":"code","source":"import random\n\ndef main():\n  # Set the initial values\n  lower_bound = 1\n  upper_bound = 100\n  target = random.randint(lower_bound, upper_bound)\n  num_guesses = 0\n  \n  print(\"I'm thinking of a number between {} and {}. Can you guess what it is?\".format(lower_bound, upper_bound))\n  \n  # Start the loop\n  while True:\n    # Get the player's guess\n    guess = input(\"Your guess: \")\n    \n    # Check if the input is valid\n    if not guess.isdigit():\n      print(\"Please enter a valid number!\")\n      continue\n    guess = int(guess)\n    \n    # Check if the guess is within bounds\n    if guess < lower_bound or guess > upper_bound:\n      print(\"Please enter a number between {} and {}!\".format(lower_bound, upper_bound))\n      continue\n    \n    # Increment the number of guesses\n    num_guesses += 1\n    \n    # Check if the guess is correct\n    if guess == target:\n      print(\"Congratulations! You guessed the correct number in {} guesses!\".format(num_guesses))\n      break\n    elif guess < target:\n      print(\"Your guess is too low. Try again!\")\n    else:\n      print(\"Your guess is too high. Try again!\")\n      \n# Start the game\nmain()","metadata":{"_uuid":"014de4fd-a51f-4512-abce-4af1a35849a1","_cell_guid":"3d92bdcf-6a47-4dfe-9377-53626b100f04","collapsed":false,"jupyter":{"outputs_hidden":false},"trusted":true},"execution_count":null,"outputs":[]}]}